const path = require('path')

const { argv } = require('yargs')
const HtmlWebpackPlugin = require('html-webpack-plugin')
const HtmlWebpackExcludeAssetsPlugin = require('html-webpack-exclude-assets-plugin')
const MiniCssExtractPlugin = require('mini-css-extract-plugin')
const IgnoreEmitPlugin = require('ignore-emit-webpack-plugin')
const TerserPlugin = require('terser-webpack-plugin')
const WebpackAssetsManifest = require('webpack-assets-manifest')
const { BundleAnalyzerPlugin } = require('webpack-bundle-analyzer')

const projectTitle = require('./package.json').displayName

const version = '0'

module.exports = {
  mode: 'production',

  // use 'source-map' for debugging and 'false' for smallest output
  // devtool: 'source-map',
  devtool: false,

  entry: {
    app: [
      './src/js/index.js',
      './src/styles/app.css',
    ],
  },

  output: {
    path: path.join(__dirname, 'build'),
    filename: `a-${version}.min.js`,
  },

  resolve: {
    alias: {
      './src': path.resolve(__dirname, 'src/'),
    },
  },

  module: {
    rules: [
      {
        test: /\.(gif|png|jpe?g)$/i,
        use: [{
          loader: 'file-loader',
          options: {
            name: '[name]-[hash].[ext]',
            outputPath: 'images/',
          },
        }],
      },

      {
        test: /\.(ogg|mp3|wav|mpe?g)$/i,
        use: [{
          loader: 'file-loader',
          options: {
            name: '[name]-[hash].[ext]',
            outputPath: 'sounds/',
          },
        }],
      },

      {
        test: /\.css$/,
        use: [
          MiniCssExtractPlugin.loader,
          {
            loader: 'css-loader',
            options: {
              importLoaders: 1,
              sourceMap: true,
              minimize: true,
            },
          },
          {
            loader: 'postcss-loader',
            options: {
              sourceMap: true,
            },
          },
        ],
      },

      {
        test: /\.js$/,
        exclude: /(node_modules|bower_components)/,
        loader: 'babel-loader',
        query: {
          presets: [['@babel/preset-env', { modules: false, targets: { esmodules: true } }]],
          plugins: [
            ['@babel/plugin-proposal-class-properties', { loose: true }],
          ],
        },
      },
    ],
  },

  optimization: {
    minimizer: [
      new TerserPlugin({
        sourceMap: true,
        terserOptions: {
          compress: { drop_console: true },
          output: { comments: false },
          mangle: {
            properties: {
              'keep_quoted': true,
              reserved: ['imageSmoothingEnabled']
            }
          }
        },
      }),
    ],
  },

  plugins: [
    new MiniCssExtractPlugin({ filename: `[name]-${version}.min.css` }),

    new HtmlWebpackPlugin({
      title: projectTitle,
      favicon: './favicon.ico',
      excludeAssets: [/styles-.*.js/],
      template: 'index.html',
    }),

    new IgnoreEmitPlugin([/.*.map.*/, /styles-.*.js/]),

    new HtmlWebpackExcludeAssetsPlugin(),

    new WebpackAssetsManifest(),

    (argv.analyze) ? new BundleAnalyzerPlugin() : () => {},
  ],
}
